import React from "react";

function TimerOnDisplay (props) {

    
    const calculatePercentage = () => {

        if(session?.label === "Focusing"){
        return session?.timeRemaining/(focusDuration*60);
        }
        else{
        return session?.timeRemaining/(breakDuration*60);
        }
   

    }

    function calculateTimeRemaining (time) {
        let min = Math.floor(time/60);
        let sec = time%60;

        sec = sec < 10 ? "0" + sec : sec;
    
        return min + ":" + sec;
    }

    if(true){
    return (
    <div>
    {/* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */}
    <div className="row mb-2">
      <div className="col">
        {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
        <h2 data-testid="session-title">
          {props.session.label} for {props.focusDuration}:00 minutes                      
        </h2>
        {/* TODO: Update message below correctly format the time remaining in the current session */}
        <p className="lead" data-testid="session-sub-title">
          {calculateTimeRemaining(props.session.timeRemaining)} remaining
        </p>
      </div>
    </div>
    <div className="row mb-2">
      <div className="col">
        <div className="progress" style={{ height: "20px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow={calculatePercentage} // TODO: Increase aria-valuenow as elapsed time increases
            style={{ width: calculatePercentage }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </div>
  </div>
    )
    }
    else{

    }
}

export default TimerOnDisplay;
